/*
	HARD RESET
*/
//Hard resets the game, used for after win/lose state
//this will return all values to a "default" state but will not create any more data. it reuses all data initialised at the start of the game
void Game::HardReset()
{

	//hard reset all objects
	//for the pools, this requires setting all of their objects to be available
	//for the enemy controller, this involves resetting the score and enemy counts, and deleting all enemies from the vector and memory
	//for the player controller, reset the player to the default state, and reset lives
	m_pPlayerController->HardReset();
	m_pEnemyController->HardReset();
	m_pParticlePixelPool->HardReset();

	//reset timers
	textCooldown = 120;
	bossWarningTime = 120;

	//reset the score
	score = 0;

	//reset the menu cooldown and switch the game back to the menu
	gameMenu->buttonCooldown = 30;
	gameMenu->gameState = E_GAME_STATE_MAIN_MENU;
	gameState = E_GAME_STATE_MAIN_MENU;

}

/*
	RENDER SCORE
*/
void Game::RenderScore(string scoreString)
{

	for (int i = 0; i < 9; i++)
	{

		//if the max amount of digits - i is LESS than the length of digits in the score, set each score digit back to front to the corresponding digit in the actual score
		if (8 - i < scoreString.length())
		{

			//get the current character
			int currentChar = scoreString.at(scoreString.length() - 1 - (8 - i)) - 48;

			//set it to the score display digit
			scoreDisplayList[i]->SetDigitValue(currentChar);

		}

		//else, render a 0
		else
		{

			scoreDisplayList[i]->SetDigitValue(0);

		}

		//render the digit
		scoreDisplayList[i]->Update(0);
		scoreDisplayList[i]->Render(m_pRenderer);

	}

}